import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Routes, useHistory, Redirect, Switch} from 'react-router-dom'

import Home from './components/Home'
import Facturas from './components/Facturas'
import Ordenes from './components/Ordenes'
import Reports from './components/Reports'
import Clientes from './components/Clientes'
import Vehiculos from './components/Vehiculos'
import Puertos from './components/Puertos'
import Tarifas from './components/Tarifas'
import DatosEmpresa from './components/DatosEmpresa'
import Dashboard from './components/Dashboard'

function requireAuth(nextState, replace, next) {
  if (this.state.loggedIn) {
    replace({
      pathname: "/login",
      state: { nextPathname: nextState.location.pathname }
    });
  }
  next();
}

export default class App extends Component {

  constructor() {
    super();
    this.state = {
      properties: [],      
      loggedIn: false,
      time: new Date().getSeconds()
    };
  }
  render() {
    console.log("app state:", this.state.loggedIn);
    return (
      <>
        {/* This is the alias of BrowserRouter i.e. Router */}
        <Router>
          <Switch>
            {/* This route is for home component 
            with exact path "/", in component props 
            we passes the imported component*/}
            <Route exact path="/" component={Home} />

            {/* This route is for about component 
            with exact path "/about", in component 
            props we passes the imported component*/}
            <Route path="/Dashboard" component={Dashboard} />

            {/* This route is for contactus component
            with exact path "/contactus", in 
            component props we passes the imported component*/}
            <Route path="/Facturass" component={facturas} />

            {/* If any route mismatches the upper 
            route endpoints then, redirect triggers 
            and redirects app to home component with to="/" */}
            <Redirect to="/" />
          </Switch>
        </Router>
      </>
    );
  }
}
