import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Routes, useHistory, Redirect, useNavigate } from 'react-router-dom'

import Home from './components/Home'
import Facturas from './components/Facturas'
import Ordenes from './components/Ordenes'
import Reports from './components/Reports'
import Clientes from './components/Clientes'
import Vehiculos from './components/Vehiculos'
import Puertos from './components/Puertos'
import Tarifas from './components/Tarifas'
import DatosEmpresa from './components/DatosEmpresa'
import Dashboard from './components/Dashboard'
import Login from './components/Login'

function requireAuth(nextState, replace, next) {
  if (this.state.loggedIn) {
    replace({
      pathname: "/login",
      state: {nextPathname: nextState.location.pathname}
    });
  }
  next();
}

export default class App extends Component {

  constructor() {
    super();
    this.state = {
      properties: [],      
      loggedIn: false,
      time: new Date().getSeconds()
    };
  }

  login = data => {
      const updatedState = { ...this.state.auth, investor: data };
      localStorage.setItem("token", data.jwt);
      this.setState({ auth: updatedState });
      this.setState({ loggedIn: true });
  };

  logout = () => {
      localStorage.removeItem("token");
      this.setState({ auth: { investor: {} } });
      this.setState({ loggedIn: false });
      this.clearCart();
  };

  render() {
    console.log("app state:", this.state.loggedIn);
    return (
      <Router>        
          <Routes>
            <Route exact path="/" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Dashboard" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Facturas" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Ordenes" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Reportes" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Clientes" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Vehiculos" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Puertos" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/Tarifas" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />
            <Route exact path="/DatosEmpresa" render={props => this.state.loggedIn ? (<Home/>) : (<navigate to="/Login" /> )} />

            <Route exact path="/Login" element={<Login />}/>
          </Routes>        
      </Router>
    );
  }
}
